import React, { Component } from 'react';


class ErrorBoundry extends Component {
	constructor(props){
		super(props);
		this.state = {
			hasError: false
		}
	}

	componentDidCatch(error, info){
		this.setState({hasError: true});
	}

	render(){
		if ( this.state.hasError){
			return <h1> error! Not Good! </h1>
		}
		return this.state.children
	}
}
